onst reGenerate = () => {\n    ref.current.reGenerate();\n  };\n\n  const getSseStatus = () => {\n    console.log(ref.current.getStatus());\n  };\n\n  return (\n    <>\n      <div className=\"chatContainer\">\n        <ChatWebkit.ChatBox\n          userAvatar={<span>TEST USER</span>}\n          modelAvatar={<span>LLM</span>}\n          tooLongErrorComponent={TooLongErrorComponent()}\n          eventName=\"\"\n          apiType={1}\n          propsSessionList={sessionList}\n          url={\"{your api or internLM api}\"}\n          token={\"this is sse header token\"}\n          requestMessageContainerStyle={{ backgroundColor: \"#ffffff\" }}\n          responseMessageContainerStyle={{\n            backgroundColor: \"rgba(39,45,230,.1)\",\n          }}\n          ref={ref}\n        />\n      </div>\n      <Button\n        style={{\n          \"background-color\": \"#4096ff\",\n          \"margin-right\": \"5px\",\n          border: \"0px\",\n          padding: \"10px\",\n          \"border-radius\": \"6px\",\n        }}\n        onClick={startSse}\n      >\n        \u5F00\u59CB\u4F1A\u8BDD\n      </Button>\n      <Button\n        style={{\n          \"background-color\": \"#4096ff\",\n          \"margin-right\": \"5px\",\n          border: \"0px\",\n          padding: \"10px\",\n          \"border-radius\": \"6px\",\n        }}\n        onClick={stopSse}\n      >\n        \u505C\u6B62\u4F1A\u8BDD\n      </Button>\n      <Button\n        style={{\n          \"background-color\": \"#4096ff\",\n          \"margin-right\": \"5px\",\n          border: \"0px\",\n          padding: \"10px\",\n          \"border-radius\": \"6px\",\n        }}\n        onClick={reGenerate}\n      >\n        \u91CD\u8BD5\n      </Button>\n      <Button\n        style={{\n          \"background-color\": \"#4096ff\",\n          \"margin-right\": \"5px\",\n          border: \"0px\",\n          padding: \"10px\",\n          \"border-radius\": \"6px\",\n        }}\n        onClick={getSseStatus}\n   